if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/alber/Downloads/Coding/Personal/stopGettingHit/node_modules/.pnpm/ansi-html-community@0.0.8/node_modules/ansi-html-community/bin/node_modules:/mnt/c/Users/alber/Downloads/Coding/Personal/stopGettingHit/node_modules/.pnpm/ansi-html-community@0.0.8/node_modules/ansi-html-community/node_modules:/mnt/c/Users/alber/Downloads/Coding/Personal/stopGettingHit/node_modules/.pnpm/ansi-html-community@0.0.8/node_modules:/mnt/c/Users/alber/Downloads/Coding/Personal/stopGettingHit/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
